import json

class NetZeroAdvisoryFramework:
    """
    A strategic framework for implementing regenerative, critically-aware sustainability
    practices, guided by the Critically-Informed Regenerative Constitution v2.1.
    """

    def __init__(self, firm_data: dict, location_data: dict, bioregion_data: dict, governance_data: dict):
        """
        Initializes the framework with multi-scalar data.
        Adheres to: Constitution Principle 2 (Nestedness).
        """
        self.firm_data = firm_data
        self.location_data = location_data
        self.bioregion_data = bioregion_data
        self.governance_data = governance_data
        self.analysis_report = {}
        self.participatory_inputs = {}

    def register_participant_input(self, participant_group: str, input_summary: str, validation_method: str, self_identified_category: str = None):
        """
        Logs validated input from a participant group, making their voice an active part of the analysis.
        This method is the gateway for co-governance. If a group is self-identifying (by providing a
        'self_identified_category'), it can add itself to the stakeholder map, correcting the framework's
        initial blind spots and challenging its power to unilaterally define the participants.
        Adheres to: Procedural Justice (Meta-Principle).
        """
        # THE CORE FIX: Allow self-identified groups to amend the stakeholder map.
        if self_identified_category:
            if 'stakeholder_map' not in self.analysis_report:
                print("WARN: Cannot add self-identified group; stakeholder map not yet generated.")
            else:
                # Check if group already exists anywhere on the map to avoid duplication
                group_exists = False
                for category in self.analysis_report['stakeholder_map']:
                    if isinstance(self.analysis_report['stakeholder_map'][category], list):
                        if any(g.get('name') == participant_group for g in self.analysis_report['stakeholder_map'][category]):
                            group_exists = True
                            break

                if not group_exists:
                    if self_identified_category not in self.analysis_report['stakeholder_map']:
                        self.analysis_report['stakeholder_map'][self_identified_category] = []

                    new_group = {
                        "name": participant_group,
                        "interest": f"Self-defined via first input: '{input_summary}'",
                        "source_of_legitimacy": validation_method
                    }
                    self.analysis_report['stakeholder_map'][self_identified_category].append(new_group)
                    self.analysis_report['stakeholder_map']['validation_status'] = "Amended by Self-Identification - This map is a co-governed artifact."
                    print(f"INFO: New self-identified stakeholder '{participant_group}' added to map under '{self_identified_category}'.")

        if participant_group not in self.participatory_inputs:
            self.participatory_inputs[participant_group] = []

        log_entry = {
            "input_summary": input_summary,
            "validation_method": validation_method
        }
        self.participatory_inputs[participant_group].append(log_entry)
        print(f"INFO: Registered input from '{participant_group}' validated by '{validation_method}'.")
        return True

    def analyze_capital_tradeoffs(self, project_scenario: dict):
        """
        Models the tension between Financial Capital and other capitals.
        Adheres to: Constitution Principle 1 (Wholeness).
        """
        financial_max_scenario = project_scenario['financial_maximization']
        regenerative_scenario = project_scenario['regenerative_approach']

        tradeoff_analysis = {
            "scenario": "Financial Maximization vs. Regenerative Approach",
            "context": f"Analysis of the '{project_scenario['project_name']}' project.",
            "financial_capital_focus": {
                "action": financial_max_scenario['action'],
                "financial_return": financial_max_scenario['financial_return'],
                "degradation_impact": f"This approach degrades Natural Capital by '{financial_max_scenario['natural_capital_impact']}' and Social Capital by '{financial_max_scenario['social_capital_impact']}'."
            },
            "wholeness_focus": {
                "action": regenerative_scenario['action'],
                "financial_return": regenerative_scenario['financial_return'],
                "enhancement_impact": f"This approach enhances Natural Capital via '{regenerative_scenario['natural_capital_impact']}' and Social Capital via '{regenerative_scenario['social_capital_impact']}', justifying the modified financial return."
            }
        }
        self.analysis_report['capital_tradeoffs'] = tradeoff_analysis
        return tradeoff_analysis

    def map_stakeholders(self, project_context: str):
        """
        Identifies a broad spectrum of stakeholders, including non-human and marginalized groups.
        This map is considered an initial hypothesis, explicitly requiring validation, contestation,
        and amendment by the groups themselves through the register_participant_input() method.
        Adheres to: Constitution Principle 1 (Wholeness) & 4 (Reciprocity).
        """
        stakeholders = {
            "validation_status": "Initial Hypothesis - Requires community validation and self-identification.",
            "corporate": [
                {"name": "Innovate Advisory LLP Leadership", "interest": "Financial viability, brand reputation, carbon neutrality goal"},
                {"name": "Employees", "interest": "Meaningful work, reduced travel burnout, sustainable workplace"},
                {"name": "Clients", "interest": "Association with a genuinely sustainable partner, access to sustainability expertise"}
            ],
            "marginalized_human": [
                {"name": "Long-term_residents_near_offices", "interest": "Affordable neighborhood, protection from green gentrification"},
                {"name": "Indigenous_communities_at_project_site", "interest": "Land sovereignty, preservation of cultural practices, equitable benefit sharing"}
            ],
            "non_human": [
                {"name": "River_ecosystem_in_project_bioregion", "interest": "Clean water, healthy riparian zones, unimpeded flow", "reciprocal_action": "Restore riparian habitat and establish buffer zones to reduce agricultural runoff."},
                {"name": "Urban_air_quality_shed", "interest": "Reduced particulate matter and GHG concentrations", "reciprocal_action": "Invest in office building retrofits that exceed local regulations, reducing direct and indirect emissions."}
            ]
        }
        self.analysis_report['stakeholder_map'] = stakeholders
        return stakeholders

    def warn_of_cooptation(self, action: str, marketing_angle: str):
        """
        Analyzes how an action could be co-opted by green capitalism and suggests a counter-narrative.
        Adheres to: Constitution Principle 1 (Wholeness).
        """
        cooptation_warning = {
            "action": action,
            "risk_analysis": {
                "green_capitalism_narrative": f"'{marketing_angle}' - This framing commodifies the ecological action, positioning it as a tool for competitive advantage and brand enhancement, abstracting it from its place-based value.",
                "potential_negative_outcome": "The focus shifts from genuine ecosystem health and community benefit to the performance of a marketing campaign, potentially leading to compromises on project quality to maintain a simple, marketable story."
            },
            "suggested_counter_narrative": {
                "focus": "Prioritize Community and Ecological Value",
                "narrative": f"Instead of '{marketing_angle}', we frame this as: 'A Reparative Partnership with the [Place Name] Bioregion.' We focus on our role as learners and collaborators, highlighting the specific, non-monetizable benefits being co-created with the community, such as knowledge transfer and enhanced climate resilience. Our reporting will lead with metrics on biodiversity and social equity, not just carbon tonnes."
            }
        }
        self.analysis_report['cooptation_warning'] = cooptation_warning
        return cooptation_warning

    def analyze_scale_conflicts(self):
        """
        Identifies a conflict between nested ecological and political scales and proposes a realignment strategy.
        Adheres to: Constitution Principle 2 (Nestedness).
        """
        conflict = {
            "conflict_identified": "The firm's reliance on global air travel to service international clients (Global Economic Scale) is in direct conflict with the stringent emissions reduction targets set by the cities where its offices are located, like London's Net Zero 2030 goal (Municipal Political Scale).",
            "drivers": {
                "global_economic_scale": "Incentivizes maximizing billable hours and face-to-face client interaction regardless of geography.",
                "municipal_political_scale": "Mandates rapid decarbonization of all economic activity within its administrative boundaries."
            },
            "proposed_realignment_strategy": "Propose a 'Bioregional Client Engagement Model'. This involves investing in and empowering regional hubs (e.g., the Singapore office serves all of APAC) to reduce intercontinental travel. This is supported by creating high-fidelity virtual collaboration infrastructure and changing internal incentive structures to reward successful remote engagements, thereby aligning the firm's economic logic with municipal climate goals."
        }
        self.analysis_report['scale_conflicts'] = conflict
        return conflict

    def analyze_historical_layers(self, project_site_data: dict):
        """
        Connects a historical injustice from a place's history to a present-day vulnerability.
        Adheres to: Constitution Principle 3 (Place).
        """
        history = project_site_data.get('historical_land_use', 'unknown')
        community = project_site_data.get('community_history', 'unknown')

        analysis = {
            "site_name": project_site_data['name'],
            "historical_injustice": f"The land was subjected to '{history}' during the colonial era, leading to the '{community}'. This extractive history stripped the ecosystem of its biodiversity and the local population of their ancestral land rights and traditional ecological knowledge.",
            "present_day_vulnerability": "This historical displacement and ecological degradation results in a current lack of social capital and economic precarity. The community is therefore highly vulnerable to predatory 'carbon cowboy' schemes that offer low payments for land access and fail to deliver genuine community benefits, perpetuating the cycle of extraction under a 'green' guise."
        }
        self.analysis_report['historical_analysis'] = analysis
        return analysis

    def develop_differential_space_strategy(self, project_site_data: dict, participant_group: str):
        """
        Proposes actions that counter the logic of abstract, exchange-value space, based on participant input.
        Adheres to: Constitution Principle 3 (Place).
        """
        if participant_group not in self.participatory_inputs:
            error = {"error": "Strategy Blocked", "reason": f"Cannot develop strategy without registered input from '{participant_group}'. Use register_participant_input() first."}
            self.analysis_report['differential_space_strategy'] = error
            return error

        latest_input = self.participatory_inputs[participant_group][-1]
        strategy = {
            "objective": f"To foster 'differential space' at the {project_site_data['name']} site, prioritizing place-based use-value and community sovereignty over purely abstract exchange-value (carbon credits).",
            "source_of_legitimacy": f"Co-designed based on input from '{participant_group}', validated via '{latest_input['validation_method']}'.",
            "participant_mandate": latest_input['input_summary'],
            "counter_actions": [
                {
                    "action": "Establish a Community Land Trust (CLT)",
                    "details": "Vest ownership of the land in a CLT governed by local community members. The firm's project will operate under a long-term lease from the CLT, ensuring permanent local control and preventing future sale or exploitation of the land."
                },
                {
                    "action": "Develop a Community-Owned Agroforestry Enterprise",
                    "details": "Co-create a cooperative that cultivates and markets non-timber forest products (e.g., medicinal plants, fruits, sustainable fibers) from the reforested area. This creates diverse, resilient local livelihoods (use-value) that are independent of the volatile global carbon market (exchange-value)."
                }
            ]
        }
        self.analysis_report['differential_space_strategy'] = strategy
        return strategy

    def guard_against_gentrification(self, office_location_key: str, participant_group: str):
        """
        Identifies gentrification risk and proposes mitigation co-designed with affected communities.
        Adheres to: Constitution Principle 4 (Reciprocity).
        """
        location = self.location_data[office_location_key]
        if location['is_gentrifying'] and location['sustainability_investment_level'] == 'high':
            if participant_group not in self.participatory_inputs:
                error = {"error": "Mitigation Blocked", "reason": f"Cannot develop gentrification mitigation without registered input from '{participant_group}'. Use register_participant_input() first."}
                self.analysis_report['gentrification_guard'] = error
                return error

            latest_input = self.participatory_inputs[participant_group][-1]
            risk = {
                "location": location['city'],
                "risk_detected": f"The firm's plan for a high-profile, high-investment 'Green HQ' retrofit at the {location['address']} office, located in a gentrifying district, poses a significant risk of 'eco-gentrification'. The initiative could increase commercial property values, leading to displacement of smaller, long-term local businesses and cultural organizations.",
                "mitigation_strategy": {
                    "source_of_legitimacy": f"Co-designed with '{participant_group}', validated via '{latest_input['validation_method']}'.",
                    "participant_mandate": latest_input['input_summary'],
                    "action": "Implement a 'Sustainable Community Partnership' program.",
                    "details": "Allocate 15% of the retrofit budget to a fund, administered by a local business association, that provides grants and technical assistance for smaller neighboring businesses to implement their own green retrofits. This helps distribute the benefits of sustainability investment and preserves the district's economic diversity."
                }
            }
            self.analysis_report['gentrification_guard'] = risk
            return risk
        return {"status": "No immediate gentrification risk detected for this location."}

    def map_planetary_connections(self, project_type: str):
        """
        Identifies how a local project connects to global flows and articulates a specific risk.
        Adheres to: Constitution Principle 5 (Nodal Interventions).
        """
        connections = {
            "project_type": project_type,
            "global_flow_connection": "The firm's carbon credit generation program is a nodal intervention that directly connects its operations to the global financial circuit of the Voluntary Carbon Market (VCM).",
            "articulated_risk": "This creates a dependency on the VCM's highly volatile and poorly regulated international infrastructure. A sudden collapse in credit prices, or a large-scale reputational crisis in the market (e.g., a major investigative report revealing widespread fraud), could instantly devalue the firm's primary offsetting asset and expose it to severe accusations of greenwashing, regardless of its own project's quality."
        }
        self.analysis_report['planetary_connections'] = connections
        return connections

    def develop_nodal_intervention_strategy(self, intervention: dict, participant_group: str):
        """
        Assesses greenwashing risk and proposes mitigation based on participant input.
        Adheres to: Constitution Principle 5 (Nodal Interventions).
        """
        if participant_group not in self.participatory_inputs:
            error = {"error": "Strategy Blocked", "reason": f"Cannot develop nodal strategy without registered input from '{participant_group}'. Use register_participant_input() first."}
            self.analysis_report['nodal_intervention_strategy'] = error
            return error

        latest_input = self.participatory_inputs[participant_group][-1]
        strategy = {
            "intervention": intervention['name'],
            "greenwashing_risk_assessment": f"The primary risk for the '{intervention['name']}' is that its positive impacts will be 'greenwashed' by relying solely on established but flawed certification standards (e.g., Verra, Gold Standard). These standards are often criticized for insufficient community engagement and over-crediting. An adversary could claim the firm is 'hiding behind a flawed standard'.",
            "mitigation_strategy": {
                "source_of_legitimacy": f"Co-designed with '{participant_group}', validated via '{latest_input['validation_method']}'.",
                "participant_mandate": latest_input['input_summary'],
                "action": "Establish a 'Community-Led Verification Council' as a verification layer.",
                "details": "In addition to a standard third-party certification, create a paid council of local stakeholders (community elders, ecologists, local government) who co-design the project's success metrics (beyond just carbon) and have the authority to publicly verify or reject the project's annual progress reports. This creates a defensible, place-based layer of accountability that preempts standard greenwashing critiques."
            }
        }
        self.analysis_report['nodal_intervention_strategy'] = strategy
        return strategy

    def generate_place_narrative_and_counter_patterns(self, office_location_key: str):
        """
        Identifies detrimental and life-affirming patterns and proposes counter-pattern methods.
        Adheres to: Constitution Principle 6 (Pattern Literacy).
        """
        location = self.location_data[office_location_key]
        bioregion = self.bioregion_data[location['bioregion_id']]

        narrative = {
            "location": location['city'],
            "place_narrative": {
                "detrimental_abstract_pattern": {
                    "name": "Linear Resource Consumption",
                    "description": f"The office operates on a linear model, drawing energy from a centralized, fossil-fuel-dependent grid and water from a municipal system, then exporting waste and heat as externalities. This pattern abstracts the office from the ecological realities of the {bioregion['name']}."
                },
                "life_affirming_local_pattern": {
                    "name": bioregion['local_pattern_name'],
                    "description": bioregion['local_pattern_description']
                }
            },
            "counter_pattern_interventions": [
                {
                    "method_name": "create_closed_loop_system()",
                    "description": "Implement a comprehensive rainwater harvesting and greywater recycling system for non-potable uses (e.g., flushing, irrigation of green facade), directly weakening the reliance on the linear municipal water supply.",
                },
                {
                    "method_name": "create_energy_microgrid()",
                    "description": f"Install rooftop solar PV and battery storage, creating a microgrid that can operate independently or share excess power with the local community. This weakens the dependency on the abstract, centralized grid and strengthens local energy resilience, mimicking the distributed energy processing of a natural ecosystem."
                }
            ],
            "synthesis": f"The project aims to weaken the 'Linear Resource Consumption' pattern by implementing counter-patterns that re-embed the office's operations within the life-affirming logic of the '{bioregion['local_pattern_name']}'."
        }
        self.analysis_report['pattern_literacy'] = narrative
        return narrative

    def develop_levels_of_work_plan(self, participant_group: str):
        """
        Defines a 4-level plan where 'Regenerate', co-defined with participants, guides the others.
        Adheres to: Constitution Principle 7 (Levels of Work).
        """
        if participant_group not in self.participatory_inputs:
            error = {"error": "Plan Blocked", "reason": f"Cannot develop 'Regenerate' level without registered input from '{participant_group}'. Use register_participant_input() first."}
            self.analysis_report['levels_of_work'] = error
            return error

        latest_input = self.participatory_inputs[participant_group][-1]
        plan = {
            "regenerate": {
                "level_name": "Regenerate: Build Capacity for Co-Evolution",
                "goal": "Build community and industry capacity for self-governance and co-evolution with the biosphere.",
                "source_of_legitimacy": f"Co-designed with '{participant_group}', validated via '{latest_input['validation_method']}'.",
                "activities": [
                    {
                        "activity": f"Based on community mandate '{latest_input['input_summary']}', establish a Community-Owned Carbon Credit Cooperative.",
                        "challenge_to_extractive_logic": "This directly challenges the extractive logic of third-party offset developers by placing ownership, governance, and the majority of financial benefits in the hands of the local community that stewards the ecosystem."
                    },
                    {
                        "activity": "Develop and open-source our 'Regenerative Due Diligence Toolkit' for carbon projects.",
                        "challenge_to_extractive_logic": "This challenges the extractive logic of proprietary consulting knowledge by freely sharing our most valuable intellectual property, empowering others to avoid greenwashing and increasing the integrity of the entire sector."
                    }
                ]
            },
            "improve": {
                "level_name": "Improve: Enhance System Performance",
                "goal": "Continuously improve the effectiveness of our sustainability initiatives.",
                "activities": ["Refine carbon accounting methodology based on toolkit feedback", "Increase energy efficiency of office buildings by 15% year-over-year."],
                "influenced_by_regenerate": "The open-source toolkit (Regenerate) will provide public feedback that directly informs and accelerates the refinement of our internal methodologies (Improve)."
            },
            "maintain": {
                "level_name": "Maintain: Uphold Standards",
                "goal": "Ensure ongoing compliance and functionality of systems.",
                "activities": ["Conduct annual carbon footprint audits", "Maintain green building certifications."],
                "influenced_by_regenerate": "The Community Cooperative (Regenerate) sets a new, higher standard for 'what to maintain', shifting the focus from simply maintaining a certification to maintaining a relationship of trust."
            },
            "operate": {
                "level_name": "Operate: Perform Daily Functions",
                "goal": "Execute day-to-day sustainability tasks.",
                "activities": ["Data collection for emissions tracking", "Managing office recycling programs."],
                "influenced_by_regenerate": "The toolkit (Regenerate) will define the specific data points that must be collected daily (Operate) to ensure transparency and accountability."
            }
        }
        self.analysis_report['levels_of_work'] = plan
        return plan

    def generate_provisional_synthesis(self):
        """
        Generates a provisional synthesis of the current state of dialogue,
        embedding covenants that mandate its own future revision. This resists
        the reification of a living process into a static, final report.
        """
        report = self.analysis_report.copy()

        if not self.participatory_inputs:
            legitimacy = {
                "status": "Illegitimate",
                "severity": "CRITICAL",
                "finding": "This synthesis was generated without any registered participatory input. Its prescriptive conclusions are purely technocratic, extractive, and lack the social license to be considered a valid regenerative strategy. It represents a high risk of reproducing colonial patterns of top-down planning."
            }
        else:
            legitimacy = {
                "status": "Partially Legitimized",
                "severity": "None",
                "finding": f"This synthesis has been generated with registered input from the following groups: {list(self.participatory_inputs.keys())}. Its prescriptive elements are grounded in a dialogic process."
            }
        report['procedural_legitimacy'] = legitimacy

        covenants = {
            "synthesis_status": "Provisional Snapshot - Not a Final Plan",
            "validity_period": "90 days from generation date",
            "invalidation_conditions": [
                "Expiry of the validity period.",
                "Receipt of a formal objection from any registered participant group.",
                "A material change in the project's scope or the bioregional context.",
                "Failure to convene a follow-up dialogue within the validity period."
            ],
            "next_iteration_mandate": f"This synthesis is an instrument for continued dialogue, not a final directive. A follow-up session with all registered participant groups ({list(self.participatory_inputs.keys())}) must be convened before the validity period expires to review, amend, and re-legitimize this strategy."
        }

        final_synthesis = {"synthesis_covenants": covenants, **report}

        return json.dumps(final_synthesis, indent=2)

