        return {
            "human_stakeholders": [
                {"name": "long_term_residents", "role": "Provide hyperlocal social data, receive optimized public services."},
                {"name": "informal_merchants", "role": "Provide commercial transaction data, receive access to micro-finance."},
                {"name": "municipal_gov", "role": "Provide policy directives, receive efficiency dashboards."}
            ],
            "non_human_stakeholders": [
                {"name": "river_ecosystem", "role": "Provide sensor data (flow, turbidity), receive predictive pollution alerts."}
            ]
        }

