        self.holistic_ledger['social_capital']['value'] = fish_biomass_estimate
        print(f"Logged ecological update: {hectares_restored} ha restored. Habitat index: {habitat_quality_index}. Fish biomass: {fish_biomass_estimate} kg.")

    def log_social_outcomes(self, youth_engagement_hours: int, inter_council_meetings: int):
        """Updates social and human capital based on community activities."""
        # Simple model: each 1000 hours of youth engagement increases human capital
        self.capitals['human'] += youth_engagement_hours / 1000.0
        self.non_monetizable_value['youth_knowledge_transfer'] += youth_engagement_hours

        # Each successful meeting increases social capital
        self.capitals['social'] += inter_council_meetings * 0.1

        # Cap capitals at 10
        self.capitals['human'] = min(self.capitals['human'], 10.0)
        self.capitals['social'] = min(self.capitals['social'], 10.0)
        print(f"Logged social outcomes: {youth_engagement_hours} youth hours, {inter_council_meetings} council meetings.")

