        if food_sovereignty_score_increase > 0:
            new_score = self.non_monetizable_value['food_sovereignty_score'] + food_sovereignty_score_increase
            self.non_monetizable_value['food_sovereignty_score'] = min(new_score, 1.0) # Cap at 1.0
