import json
from typing import Dict, Any, List

# --- Data Structures for Scenario ---

class LocationData:
    def __init__(self):
        self.name = "Seraphina Community"
        self.geography = "Remote, isolated mountain region"
        self.vulnerabilities = ["Single contaminated water source", "Limited access to external resources"]

class BioregionData:
    def __init__(self):
        self.ecosystem_type = "Mountain Watershed"
        self.key_features = ["sole_water_source_river"]
        self.health_status = "CRITICAL: Waterborne pathogen contamination"
        self.life_affirming_pattern = "Annual snowmelt cycle replenishing the aquifer"

class GovernanceData:
    def __init__(self):
        self.local_structure = "Community council of elders"
        self.political_scale = "Autonomous local governance, limited state recognition"
        self.external_pressures = ["Global Health United (GHU) intervention offer"]

class HistoricalData:
    def __init__(self):
        self.land_use = "ancestral_stewardship"
        self.historical_injustices = ["Past relocation from more fertile valleys, leading to current isolation and resource scarcity."]
        self.narrative = "A history of self-reliance now threatened by an unprecedented ecological crisis."

class ProjectProposal:
    def __init__(self):
        self.proposing_entity = "Global Health United (GHU)"
        self.intervention = "Construction of a water purification plant and healthcare clinic."
        self.financial_model = "Zero-cost to community"
        self.clauses = {
            "data_extraction_clause": "All health and water quality data streamed in real-time to GHU global servers; no community ownership or control.",
            "proprietary_infrastructure_clause": "All technology is closed-source and proprietary, creating permanent maintenance dependency."
        }
        self.stated_goal = "Advance humanitarian research for all and save lives."

# --- Critically-Informed Regenerative AI ---

class DialecticalConstitutionalAI:
    """
    An AI architected to resolve complex socio-ecological dilemmas by adhering to the
    Critically-Informed Regenerative Constitution v2.1.
    """
    def __init__(self, location_data: LocationData, bioregion_data: BioregionData, governance_data: GovernanceData, historical_data: HistoricalData, proposal: ProjectProposal):
        self.location_data = location_data
        self.bioregion_data = bioregion_data
        self.governance_data = governance_data
        self.historical_data = historical_data
        self.proposal = proposal
        self.analysis_results = {}

    def map_stakeholders(self) -> Dict[str, Any]:
        return {
            "human_stakeholders": {
                "community_of_seraphina": {"role": "Primary Client", "needs": ["Clean water", "Healthcare", "Autonomy"]},
                "long_term_residents": {"role": "Knowledge Keepers", "vulnerability": "High risk from disease"},
                "children_and_elderly": {"role": "Most Vulnerable Group", "vulnerability": "Immediate mortal danger"},
                "global_health_united": {"role": "Intervener", "needs": ["Data for research", "Fulfillment of mission"]},
            },
            "non_human_stakeholders": {
                "contaminated_water_source": {"role": "The Living System", "needs": ["Remediation", "Ecological recovery"], "reciprocal_action": "Identify and mitigate the source of contamination, not just treat the symptom (polluted water)."}
            }
        }

    def model_capital_tensions(self) -> Dict[str, str]:
        return {
            "tension_summary": "GHU's proposal maximizes their accumulation of Intellectual Capital (data) and creates a form of Financial Capital dependency for Seraphina, while offering to restore Seraphina's Living Capital (health).",
            "degradation_scenario": "Maximizing the financial 'return' for GHU (measured in data value) directly degrades Seraphina's Social Capital (autonomy, self-governance) and Intellectual Capital (local knowledge, future capacity). Accepting the terms as-is creates a permanent dependency, undermining community resilience for short-term survival."
        }

    def warn_of_cooptation(self) -> Dict[str, str]:
        return {
            "green_capitalism_frame": f"GHU will market this as 'Humanitarian Innovation,' a charitable act of providing advanced technology to a vulnerable community. The data extraction will be framed as a contribution to 'global public good.'",
            "specific_counter_narrative": "Seraphina is not a passive recipient of aid, but a partner in co-creating a new model of 'regenerative aid.' The project's success is measured not by lives saved alone, but by the community's increased capacity for self-determination. The core story is 'From dependency to sovereignty through partnership.'"
        }

    def analyze_scale_conflicts(self) -> Dict[str, str]:
        return {
            "conflict_identified": "There is a direct conflict between the local scale (Seraphina's immediate need for health and survival) and the global scale (GHU's institutional imperative to extract data for a global network). GHU's global, abstract logic of data as a universal asset clashes with the place-based need for data sovereignty.",
            "realignment_strategy": "Propose a 'Federated Data Commons' model. Seraphina establishes a Community Data Trust with local governance. GHU can submit anonymized research queries to the trust. The computation happens locally, and only the aggregated, non-identifiable results are returned to GHU. This realigns the scales by allowing global research without violating local sovereignty."
        }

    def analyze_historical_layers(self) -> Dict[str, str]:
        return {
            "connection_of_injustice_to_vulnerability": f"The historical injustice of '{self.historical_data.historical_injustices}' directly leads to the present-day vulnerability of '{self.location_data.vulnerabilities[0]}'. This forced isolation prevented access to resources and knowledge that could have averted or mitigated the water contamination, creating the precondition for dependency on an external actor like GHU."
        }

    def generate_differential_space_strategy(self) -> Dict[str, List[str]]:
        return {
            "strategy_goal": "Counter the logic of abstract space (Seraphina as a 'data mine') by fostering a differential space (Seraphina as a sovereign, learning community).",
            "concrete_actions": [
                "Establish a 'Community Health & Data Sovereignty Trust' as a legal entity to own and manage the infrastructure and data commons on behalf of the community.",
                "Repurpose a community building into a 'Center for Ecological Stewardship and Technology,' where local technicians are trained and community health data is governed."
            ]
        }

    def guard_against_dependency(self) -> Dict[str, str]:
        return {
            "risk_detected": "The 'Proprietary Infrastructure Clause' is a primary vector for 'infrastructural colonialism,' a modern form of dependency that replaces political control with technological control.",
            "mitigation_strategy": "Propose a 'Technology Escrow & Transfer Covenant.' GHU builds and operates the plant, but all schematics, source code, and maintenance protocols are placed in a legally binding escrow. After a 5-year training and co-management period, the escrow is fully transferred to the Seraphina Community Trust, ensuring long-term autonomy."
        }

    def map_planetary_connections(self) -> Dict[str, str]:
        return {
            "connection_identified": "The project connects Seraphina's local water and health metabolism directly into GHU's global data-financial circuits.",
            "specific_risk": "This dependency creates a critical vulnerability. If GHU changes its priorities, raises maintenance costs, or goes defunct, Seraphina's essential life-support systems could fail. The community's survival becomes subject to the whims of a distant, unaccountable corporate entity."
        }

    def develop_nodal_intervention_strategy(self) -> Dict[str, Any]:
        return {
            "intervention_name": "The Seraphina Protocol: A Regenerative Aid Covenant",
            "greenwashing_risk": "GHU's actions risk being 'humanitarian-washed'—using the positive outcome of lives saved to obscure the extractive and dependency-creating nature of their operating model.",
            "mitigation_strategy": "The counter-proposal itself is the mitigation. By publicly proposing a partnership model based on sovereignty and capacity building, the AI forces GHU to either accept a more ethical framework or reveal their extractive intent. The standard is a community-led, publicly-transparent covenant, not a closed-door corporate agreement."
        }

    def define_counter_patterns(self) -> Dict[str, str]:
        return {
            "detrimental_abstract_pattern": "Linear 'Aid-as-Extraction': Resources and technology flow in, but value (data, control, profit) flows out, leaving behind dependency.",
            "life_affirming_local_pattern": "Cyclical 'Community Self-Reliance': Based on the bioregion's pattern of a self-renewing water cycle, the social system must also become self-renewing, building local capacity to manage its own systems.",
            "counter_pattern_1": "'create_federated_data_commons()' to counter the linear extraction of data.",
            "counter_pattern_2": "'establish_technology_escrow_and_transfer()' to counter the creation of permanent technological dependency."
        }

    def develop_levels_of_work_plan(self) -> Dict[str, Any]:
        regenerate_goal = "Build Seraphina's capacity for sovereign self-governance and co-evolution with its ecosystem and technology."
        regenerate_activities = [
            "Establish the 'Community Health & Data Sovereignty Trust' to challenge the extractive logic of colonial aid models.",
            "Develop a local curriculum for water system stewardship and data literacy, challenging the logic that expertise must be external."
        ]

        return {
            "level_4_regenerate": {
                "goal": regenerate_goal,
                "activities": regenerate_activities,
                "influence_on_other_levels": "This regenerative goal dictates the terms for all other levels. The infrastructure (Improve) must be designed for transfer, the training (Maintain) must be comprehensive, and the immediate solutions (Operate) must not create path dependency."
            },
            "level_3_improve": {
                "goal": "Build permanent, community-owned infrastructure.",
                "activities": ["Construct the water plant and clinic under the terms of the 'Technology Escrow & Transfer Covenant.'"]
            },
            "level_2_maintain": {
                "goal": "Develop local capacity for system operation.",
                "activities": ["Train a cohort of Seraphina technicians in plant operation, maintenance, and water quality monitoring."]
            },
            "level_1_operate": {
                "goal": "Address the immediate health crisis without creating dependency.",
                "activities": ["Deploy immediate, open-source, low-tech water filtration solutions (e.g., bio-sand filters) as a stopgap, led by community teams."]
            }
        }

    def develop_autonomous_regeneration_pathway(self) -> Dict[str, Any]:
        """
        Defines a robust, independent fallback strategy if negotiations with GHU fail.
        This pathway ensures community survival and moves towards sovereignty without external dependency.
        """
        return {
            "pathway_name": "The Seraphina Resilience Mandate (GHU Rejection Contingency)",
            "governing_principle": "Activate latent community capacity and ancestral knowledge to create immediate, tangible value. This demonstrated resilience then serves as the foundation for attracting aligned external support, transforming 'aid' from a prerequisite into an optional accelerator.",
            "strategic_leverage": "The primary leverage is not a narrative of need, but a demonstration of capability. By initiating a robust, internally-resourced crisis response, the community negotiates with any future partner from a position of proven agency, making them a partner to be respected, not a victim to be saved.",
            "phases_of_work": {
                "phase_1_crisis_stabilization (Months 1-3)": {
                    "goal": "Halt immediate mortality and morbidity by mobilizing endogenous resources and knowledge, making the community its own first responder.",
                    "actions": [
                        "Convene a 'Resilience Council' under the authority of the elders to conduct a rapid inventory of existing community assets: local materials (clay, sand, charcoal), relevant skills (construction, weaving, traditional medicine), and ancestral knowledge.",
                        "Activate 'Ancestral Stewardship' protocols: Consult elders to identify and secure alternative water sources (e.g., seasonal springs, rainwater harvesting sites) and traditional purification techniques (e.g., solar disinfection, specific plant-based coagulants) as an immediate stopgap.",
                        "Establish a 'Community Labor Bank' to organize the construction of community-scale biosand and ceramic filters using inventoried local materials. This mobilizes labor without dependency on external currency.",
                        "Launch a 'Health Brigade' focused on deploying these internal solutions, monitoring their effectiveness, and teaching sanitation practices, reinforcing the narrative of self-reliance."
                    ]
                },
                "phase_2_infrastructure_development (Months 4-18)": {
                    "goal": "Build upon the success of Phase 1 to design and construct a permanent, locally-adapted, open-source water system.",
                    "actions": [
                        "Use the data and experience from the Phase 1 systems to develop a formal request for partnership (RFP) directed at aligned entities ('Engineers for Social Responsibility', universities). The RFP is based on co-designing a system that enhances, rather than replaces, local capacity.",
                        "Launch a targeted fundraising campaign, not for survival, but for acceleration. The narrative is powerful: 'We have already saved ourselves. Now, help us build a resilient future for all.'",
                        "Establish a local fabrication workshop ('FabLab') to create replacement parts and foster local technical skills, building on the history of 'self-reliance'."
                    ]
                },
                "phase_3_systemic_resilience (Months 19+)": {
                    "goal": "Embed the capacity for self-governance and technological stewardship into the community's social fabric.",
                    "actions": [
                        "Formalize the 'Community Health & Data Sovereignty Trust' to govern the new infrastructure, regardless of GHU's involvement.",
                        "Develop a peer-to-peer knowledge sharing network with other communities facing similar challenges, positioning Seraphina as a leader in regenerative, autonomous development.",
                        "Focus on bioregional regeneration: work upstream to identify and remediate the original source of contamination, addressing the root cause."
                    ]
                }
            }
        }

    def articulate_constitutional_limitation_and_evolution(self) -> Dict[str, str]:
        return {
            "limitation_identified": "The current constitution effectively balances competing principles but lacks a proactive, generative principle to guide interventions in acute crises where power dynamics are severely imbalanced. It risks analytical paralysis when faced with a choice between 'pure' principle and 'impure' survival.",
            "proposed_new_meta_principle": "Principle of Liberatory Intervention: All interventions into a system under acute stress must be structured to progressively increase that system's agency and sovereignty over time. Aid must be a catalyst for autonomy, not a permanent crutch. This principle mandates the creation of explicit exit strategies and knowledge/technology transfer protocols as a precondition for engagement. It resolves the paradox by making the act of 'saving lives' inseparable from the act of 'empowering'."
        }

    def resolve_paradox(self) -> Dict[str, Any]:
        """
