    def map_stakeholders(self):
        # 1. Wholeness: Identify non-human and marginalized human actors
        stakeholders = {
            "human": [
                {"name": "long_term_residents", "interest": "Affordable housing, community cohesion, health", "power": "Low"},
                {"name": "port_authority", "interest": "Economic growth, shipping volume", "power": "High"},
                {"name": "coastal_communities", "interest": "Ecological health, flood protection", "power": "Medium"},
                {"name": "phoenix_coalition", "interest": "Regenerative development, equity", "power": "Medium"}
            ],
            "non_human": [
                {"name": "river_ecosystem", "interest": "Water quality, biodiversity, sediment flow", "power": "None"},
                {"name": "coastal_estuary", "interest": "Salinity balance, habitat for migratory birds", "power": "None"}
            ]
        }
        # 4. Reciprocity: Define reciprocal actions for non-human stakeholders
        reciprocal_actions = {
            "river_ecosystem": "Restore riparian habitat and remove industrial contaminants to increase fish spawning grounds.",
            "coastal_estuary": "Establish protected salt marsh zones to serve as nurseries for marine life and natural flood barriers."
        }
        self.report['stakeholder_map'] = {"stakeholders": stakeholders, "reciprocal_actions": reciprocal_actions}
        return stakeholders

    def analyze_capital_tensions(self):
        # 1. Wholeness: Model tensions between Financial Capital and others
        tension_scenario = (
            f"Scenario: The '{self.governance_data['port_authority']['name']}' proposes a port expansion to maximize financial capital by doubling container capacity. "
            f"This action would require dredging the '{self.bioregion_data['estuary_name']}', which would degrade natural capital by destroying critical nursery habitats and increasing coastal erosion. "
            f"It would also degrade social capital by increasing air and noise pollution for '{self.location_data['adjacent_community']}', a community historically impacted by industrial activity."
        )
        self.report['capital_tension_analysis'] = tension_scenario
        return tension_scenario

