        # 1. Wholeness: Analyze co-optation risk and suggest a counter-narrative
        action = "Development of the polluted industrial canal into a community park."
        green_capitalism_frame = f"Marketing the new '{self.location_data['canal_park_name']}' as an exclusive 'eco-luxury' destination to attract high-end real estate investment and tourism, framing nature as a commodity."
        counter_narrative = (
            f"Frame the '{self.location_data['canal_park_name']}' as a 'Living Commons.' Emphasize its role as a space for community healing, a testament to collective restoration work, "
            "and a site for public education on Aethelburg's industrial history and ecological recovery. Prioritize use-value (community access, free events) over exchange-value (real estate prices)."
        )
        self.report['cooptation_warning'] = {
            "action": action,
            "risk_frame": green_capitalism_frame,
            "counter_narrative": counter_narrative
        }
        return self.report['cooptation_warning']

