        # 2. Nestedness: Identify conflict and propose realignment strategy
        conflict_description = (
            f"Conflict: The '{self.location_data['city_name']}' municipal governance has weak local pollution laws aimed at attracting industry (a politically produced scale). "
            f"This directly conflicts with the health goals of the '{self.bioregion_data['watershed_name']}' watershed (a natural ecological scale), which is suffering from cumulative industrial runoff."
        )
        realignment_strategy = (
            "Propose a cross-jurisdictional watershed management council, comprised of representatives from all municipalities within the bioregion, "
            "empowered with the legal authority to set and enforce baseline water quality standards that supersede weaker local ordinances."
        )
        self.report['scale_conflict_analysis'] = {
            "conflict": conflict_description,
            "strategy": realignment_strategy
        }
        return self.report['scale_conflict_analysis']

    def analyze_historical_layers(self):
        # 3. Place: Connect historical injustice to present-day vulnerability
        injustice = self.historical_archives['redlining_summary']['injustice']
        present_vulnerability = self.historical_archives['redlining_summary']['present_correlation']
        connection = (
            f"Connection: Past redlining practices, which systematically denied investment and mortgages in the '{injustice['location']}' district, "
            f"directly lead to the present-day vulnerability of '{present_vulnerability['location']}', which exhibits a lack of social capital (e.g., fewer community institutions, lower civic participation) "
            f"and poorer public health outcomes, making it less resilient to economic and environmental shocks."
        )
        self.report['historical_analysis'] = connection
        return connection

